from selenium import webdriverfrom time import sleepif __name__ == '__main__':    # 实例化浏览器对象    bro = webdriver.Chrome(executable_path='./chromedriver')    url = 'https://www.taobao.com/'    bro.get(url)    page_text = bro.page_source    # 标签定位    search_input = bro.find_element(by='id', value='q')    # 标签交互    search_input.send_keys('Iphone')    # 执行一组js程序    # bro.execute_script('window.scrollTo(0,document.body.scrollHeight)')    sleep(3)    # 点击搜索按钮    btn = bro.find_element(by='xpath', value='//*[@id="J_TSearchForm"]/div[1]/button')    btn.click()    bro.get('https://www.baidu.com')    sleep(2)    # 回退    bro.back()    sleep(2)    # 前进    bro.forward()    sleep(5)