# 无头浏览器from selenium.webdriver.chrome.options import Optionsfrom selenium import webdriverfrom time import sleep# 实现无可视化界面from selenium.webdriver.chrome.service import Service as ChromeService# 实现规避检测from selenium.webdriver import ChromeOptionsif __name__ == '__main__':    # # 无可视化    # chrome_options = Options()    # chrome_options.add_argument('--headless')    # chrome_options.add_argument('--disable-gpu')    # 规避检测    options = webdriver.ChromeOptions()    options.add_experimental_option('excludeSwitches', ['enable-automation'])    options.add_experimental_option('useAutomationExtension', False)    options.add_argument('--headless')    options.add_argument('--disable-gpu')    service = ChromeService(executable_path='./chromedriver')    driver = webdriver.Chrome(service=service, options=options)    driver.get('https://www.baidu.com')    page_text = driver.page_source    with open('./baidu.html', 'w', encoding='utf-8') as fp:        fp.write(page_text)    sleep(1)    driver.quit()