# 实现使用 selenium 包含验证码的登录import ddddocrfrom time import sleepfrom selenium import webdriverif __name__ == '__main__':    # 实例化浏览器对象    driver = webdriver.Chrome(executable_path='../动态加载数据处理/chromedriver')    driver.get('https://so.gushiwen.cn/user/login.aspx?from=http://so.gushiwen.cn/user/collect.aspx')    sleep(0.5)    username = driver.find_element(by='xpath', value='//*[@id="email"]')    username.send_keys('15023119606')    sleep(0.5)    password = driver.find_element(by='xpath', value='//*[@id="pwd"]')    password.send_keys('z15023119606')    # 将验证码图片保存到本地    img_code = driver.find_element(by='xpath', value='//*[@id="imgCode"]')    img_code.screenshot('./imgData2.png')    # 识别验证码图片    ocr = ddddocr.DdddOcr()    with open('imgData2.png', 'rb') as fp:        img_bytes = fp.read()    code = ocr.classification(img_bytes)    print('验证码为:' + code)    sleep(0.5)    codeInput = driver.find_element(by='xpath', value='//*[@id="code"]')    codeInput.send_keys(code)    submitBtn = driver.find_element(by='xpath', value='//*[@id="denglu"]')    submitBtn.click()    driver.quit()